// Challenge: Mad Libs
// As a developer, I have been tasked with recreating the children's game Mad Libs as an app. Mad Libs requires the user to supply a series of random words that match the part of speech as indicated. When all the words have been supplied, the user can see their words added to a pre-written story. Hilarity ensues.

// Mad Libs

// As a user, I can see a page with many text inputs.
// As a user, I can see labels next to the text inputs indicating what part of speech (or type of word) should be entered into each text form: nouns, pronouns, verbs, adjectives, and adjectives.
// As a user, I can fill out the inputs.
// As a user, after I have filled out the text inputs I can click a 'Submit' button.
// As a user, when I click 'Submit' I see a paragraph appear on the page that contains the words I entered in the text forms to create a funny story.

import './App.css';
import React, { Component } from 'react'

class App extends Component {
  constructor(props) {
    super(props) 
    this.state = {
      form: {
        noun: [],
        verb: [1, 2, 3, 4],
        adjective: [1, 2, 3, 4, 5]
      }
    }
  }

  handleChange = (e) => {
    const { form } = this.state
    alert(e.target.value)
                  //NOUN      //INPUT
    form[e.target.name] = e.target.value
      //alert(form.name)
    this.setState({ form: form})
  }

  render(){
    return(
      <div className="App-header">

        <React.Fragment>
          <form>
            <label>Noun</label>
            <input  
              type="text" 
              name="noun"
              value={this.state.form.noun}
              onChange={ this.handleChange }
            />
            <br/>
            <label>Noun</label>
            <input 
              type="text" 
              name="noun"
              value={this.state.form.noun[1]}
              onChange={ this.handleChange } 
            />
            <br/>
            <label>Noun</label>
            <input 
              type="text" 
              name="noun"
              value={this.state.form.noun[2]}
              onChange={ this.handleChange } 
            />
            <br/>
            <label>Noun</label>
            <input 
              type="text"
              name="noun" 
              value={this.state.form.noun[3]}
              onChange={ this.handleChange } 
            />

            <br/>
            <label>Verb</label>
            <input 
              type="text"
              name="verb" 
              value={this.state.form.verb[0]}
              onChange={ this.handleChange } 
            />
            <br/>
            <label>Verb</label>
            <input 
              type="text"
              name="verb" 
              value={this.state.form.verb[1]} 
              onChange={ this.handleChange }
            />

            <br/>
            <label>Adjective</label>
            <input 
              type="text" 
              name="adjective"
              value={this.state.form.adjective[0]}
              onChange={ this.handleChange } 
            />
            <br/>
            <label>Adjective</label>
            <input 
              type="text"
              name="adjective" 
              value={this.state.form.adjective[1]}
              onChange={ this.handleChange } 
            />
          </form>

          <input
            type="submit"
            value="Submit Form"
            onClick={ this.handleSubmit }
            onChange={ this.handleChange }
          />
        </React.Fragment>
      </div>
    )
  }
}

export default App;
